       collect (list rank (length (mongo-find &lt;span style="color: #008000;"&gt;"contacts"&lt;/span&gt; &lt;span style="color: #008000;"&gt;"contacts"&lt;/span&gt;
                                              `((RANK . ,rank)))))))
