        &lt;span style="color: #BA36A5;"&gt;url&lt;/span&gt;={&lt;span style="color: #006DAF; text-decoration: underline;"&gt;http://iopscience.iop.org/article/10.1088/2632-2153/ad37f0&lt;/span&gt;},
