  &lt;span style="color: #BA36A5;"&gt;url&lt;/span&gt; =
                  "http://www.sciencedirect.com/science/article/pii/S0039602815003052",
  &lt;span style="color: #BA36A5;"&gt;issn&lt;/span&gt; =         "0039-6028",
