&lt;pre class="src src-python"&gt;&lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; requests &lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; json &lt;span style="color: #0000FF;"&gt;from&lt;/span&gt; my_scopus &lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; MY_API_KEY &lt;span style="color: #BA36A5;"&gt;resp&lt;/span&gt; = requests.get(&lt;span style="color: #008000;"&gt;"http://api.elsevier.com/content/search/scopus?query=AU-ID(7004212771)&amp;amp;field=eid,aggregationType&amp;amp;count=100"&lt;/span&gt;, &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; headers={&lt;span style="color: #008000;"&gt;'Accept'&lt;/span&gt;:&lt;span style="color: #008000;"&gt;'application/json'&lt;/span&gt;, &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #008000;"&gt;'X-ELS-APIKey'&lt;/span&gt;: MY_API_KEY})
