&lt;div id="outline-container-sec-8" class="outline-2"&gt; &lt;h2 id="sec-8"&gt;&lt;span class="section-number-2"&gt;8&lt;/span&gt; And my html bibliography&lt;/h2&gt; &lt;div class="outline-text-2" id="text-8"&gt; &lt;p&gt; This generates my blog bibliography page.. &lt;/p&gt; &lt;div class="org-src-container"&gt;
&lt;pre class="src src-python"&gt;&lt;span style="color: #0000FF;"&gt;from&lt;/span&gt; scopus_xml &lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; *
&lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; requests &lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; json &lt;span style="color: #0000FF;"&gt;from&lt;/span&gt; my_scopus &lt;span style="color: #0000FF;"&gt;import&lt;/span&gt; MY_API_KEY &lt;span style="color: #BA36A5;"&gt;resp&lt;/span&gt; = requests.get(&lt;span style="color: #008000;"&gt;"http://api.elsevier.com/content/search/scopus?query=AU-ID(7004212771)&amp;amp;field=eid,aggregationType&amp;amp;count=100"&lt;/span&gt;, &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; headers={&lt;span style="color: #008000;"&gt;'Accept'&lt;/span&gt;:&lt;span style="color: #008000;"&gt;'application/json'&lt;/span&gt;, &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #008000;"&gt;'X-ELS-APIKey'&lt;/span&gt;: MY_API_KEY})
&lt;span style="color: #BA36A5;"&gt;results&lt;/span&gt; = resp.json()
&lt;span style="color: #BA36A5;"&gt;data&lt;/span&gt; = [[&lt;span style="color: #006FE0;"&gt;str&lt;/span&gt;(r[&lt;span style="color: #008000;"&gt;'eid'&lt;/span&gt;]), &lt;span style="color: #006FE0;"&gt;str&lt;/span&gt;(r[&lt;span style="color: #008000;"&gt;'prism:aggregationType'&lt;/span&gt;])] &lt;span style="color: #0000FF;"&gt;for&lt;/span&gt; r &lt;span style="color: #0000FF;"&gt;in&lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; results[&lt;span style="color: #008000;"&gt;'search-results'&lt;/span&gt;][&lt;span style="color: #008000;"&gt;"entry"&lt;/span&gt;] &lt;span style="color: #0000FF;"&gt;if&lt;/span&gt; &lt;span style="color: #006FE0;"&gt;str&lt;/span&gt;(r[&lt;span style="color: #008000;"&gt;'prism:aggregationType'&lt;/span&gt;]) == &lt;span style="color: #008000;"&gt;'Journal'&lt;/span&gt;]
&lt;span style="color: #0000FF;"&gt;with&lt;/span&gt; &lt;span style="color: #006FE0;"&gt;open&lt;/span&gt;(&lt;span style="color: #008000;"&gt;'../publications.html.mako'&lt;/span&gt;, &lt;span style="color: #008000;"&gt;'w'&lt;/span&gt;) &lt;span style="color: #0000FF;"&gt;as&lt;/span&gt; f: &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; f.write(&lt;span style="color: #008000;"&gt;'''&amp;lt;%inherit file="_templates/site.mako" /&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;article class="page_box"&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;%self:filter chain="markdown"&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;h1&amp;gt;Online collections of our work&amp;lt;/h1&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;Pick your favorite:&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;ul&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="http://orcid.org/0000-0003-2625-9232"&amp;gt;orcid:0000-0003-2625-9232&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="http://www.researcherid.com/rid/A-2363-2010"&amp;gt;researcherid:A-2363-2010&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="http://www.scopus.com/authid/detail.url?origin=AuthorProfile&amp;amp;authorId=7004212771"&amp;gt;scopusid:7004212771&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="https://scholar.google.com/citations?user=jD_4h7sAAAAJ"&amp;gt;Google Scholar&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="https://www.researchgate.net/profile/John_Kitchin"&amp;gt;Research Gate&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;li&amp;gt;&amp;lt;a href="https://www.growkudos.com/profiles/40205"&amp;gt;Kudos&amp;lt;/a&amp;gt;&amp;lt;/li&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;/ul&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;h1&amp;gt;Publications&amp;lt;/h1&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;The authors are linked to their Scopus page, the title linked to the Scopus abstract, the journal linked to the Scopus journal page, and the DOI is linked to https://doi.org which normally redirects you to the journal page.&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;ol reversed="reversed"&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;'''&lt;/span&gt;)
&lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #0000FF;"&gt;for&lt;/span&gt; eid,&lt;span style="color: #006FE0;"&gt;type&lt;/span&gt; &lt;span style="color: #0000FF;"&gt;in&lt;/span&gt; data: &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; f.write(&lt;span style="color: #008000;"&gt;'&amp;lt;li&amp;gt;{}&amp;lt;/li&amp;gt;'&lt;/span&gt;.&lt;span style="color: #006FE0;"&gt;format&lt;/span&gt;(get_html_citation(eid))) &lt;span style="color: #9B9B9B; background-color: #EDEDED;"&gt; &lt;/span&gt; f.write(&lt;span style="color: #008000;"&gt;'''&amp;lt;/ol&amp;gt;&lt;/span&gt;
&lt;span style="color: #008000;"&gt;&amp;lt;/%self:filter&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;&amp;lt;/article&amp;gt;&lt;/span&gt; &lt;span style="color: #008000;"&gt;'''&lt;/span&gt;) &lt;/pre&gt; &lt;/div&gt; &lt;/div&gt; &lt;/div&gt;
&lt;div id="outline-container-sec-9" class="outline-2"&gt; &lt;h2 id="sec-9"&gt;&lt;span class="section-number-2"&gt;9&lt;/span&gt; Summary&lt;/h2&gt; &lt;div class="outline-text-2" id="text-9"&gt; &lt;p&gt; The XML format is not that intuitive to me. It takes some practice writing robust code, e.g. sometimes the find command does not find anything, and then there is not text attribute to get, so you should check for success on finding things. Also, some text is unicode, and you have to take care to encode it, which my library does not do uniformly. Finally, not all journals have things like volume or issue. My formatting code is not super flexible, so these bibliography entries show None in them occasionally. Still, it is not too bad, and this enables a lot of analysis of your publications, as well as displaying them in different ways. See the result of this page here: &lt;a href="http://kitchingroup.cheme.cmu.edu/publications.html"&gt;http://kitchingroup.cheme.cmu.edu/publications.html&lt;/a&gt; &lt;/p&gt; &lt;/div&gt; &lt;/div&gt; &lt;p&gt;Copyright (C) 2015 by John Kitchin. See the &lt;a href="/copying.html"&gt;License&lt;/a&gt; for information about copying.&lt;p&gt;&lt;p&gt;&lt;a href="/org/2015/04/06/Using-the-Scopus-api-with-xml-output.org"&gt;org-mode source&lt;/a&gt;&lt;p&gt;&lt;p&gt;Org-mode version = 8.2.10&lt;/p&gt;

&lt;p&gt;
A given article entry in a bibliography might have the following kinds of links in it. I think we can generate these from a Scopus query.
&lt;/p&gt;

&lt;ul class="org-ul"&gt;
&lt;li&gt;Author page: &lt;a href="http://www.scopus.com/authid/detail.url?origin=AuthorProfile&amp;authorId=7004212771"&gt;http://www.scopus.com/authid/detail.url?origin=AuthorProfile&amp;authorId=7004212771&lt;/a&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.scopus.com/record/display.url?eid=2-s2.0-84901638552&amp;origin=resultslist&amp;sort=plf-f&amp;src=s&amp;sid=66816438A83B9A7B72A35BF872F8E10E.WeLimyRvBMk2ky9SFKc8Q%3a50&amp;sot=autdocs&amp;sdt=autdocs&amp;sl=17&amp;s=AU-ID%287004212771%29&amp;relpos=4&amp;relpos=4&amp;citeCnt=2&amp;searchTerm="&gt;Document page&lt;/a&gt;
&lt;/li&gt;
&lt;li&gt;Journal page: &lt;a href="http://www.scopus.com/source/sourceInfo.url?sourceId=22746&amp;origin=resultslist"&gt;http://www.scopus.com/source/sourceInfo.url?sourceId=22746&amp;origin=resultslist&lt;/a&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.scopus.com/results/citedbyresults.url?sort=plf-f&amp;cite=2-s2.0-84901638552&amp;src=s&amp;imp=t&amp;sid=66816438A83B9A7B72A35BF872F8E10E.WeLimyRvBMk2ky9SFKc8Q%3a230&amp;sot=cite&amp;sdt=a&amp;sl=0&amp;origin=resultslist&amp;editSaveSearch=&amp;txGid=66816438A83B9A7B72A35BF872F8E10E.WeLimyRvBMk2ky9SFKc8Q%3a23"&gt;Cited by&lt;/a&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href="https://doi.org/10.1016/j.catcom.2013.10.028"&gt;https://doi.org/10.1016/j.catcom.2013.10.028&lt;/a&gt;
&lt;/li&gt;
&lt;li&gt;&lt;a href="https://www.scopus.com/results/citedbyresults.url?sort=r-f&amp;src=s&amp;mltEid=2-s2.0-84901638552&amp;mltType=ref&amp;mltAll=t&amp;imp=t&amp;sid=66816438A83B9A7B72A35BF872F8E10E.WeLimyRvBMk2ky9SFKc8Q%3a290&amp;sot=mlt&amp;sdt=mlt&amp;sl=730&amp;s=REFEID%28%28%222-s2.0-79953651013%22%29+OR+%28%222-s2.0-84855602300%22%29+OR+%28%222-s2.0-33750453016%22%29+OR+%28%222-s2.0-33645349847%22%29+OR+%28%222-s2.0-0034814842%22%29+OR+%28%222-s2.0-77955464573%22%29+OR+%28%222-s2.0-13444307808%22%29+OR+%28%222-s2.0-53349153662%22%29+OR+%28%222-s2.0-80051809046%22%29+OR+%28%222-s2.0-84876703352%22%29+OR+%28%222-s2.0-80053316882%22%29+OR+%28%222-s2.0-83255187152%22%29+OR+%28%222-s2.0-80052944171%22%29+OR+%28%222-s2.0-84865730756%22%29+OR+%28%222-s2.0-0000778362%22%29+OR+%28%222-s2.0-60349090198%22%29+OR+%28%222-s2.0-2442537377%22%29+OR+%28%222-s2.0-0030190741%22%29+OR+%28%222-s2.0-25744460922%22%29+OR+%28%222-s2.0-0011236321%22%29+OR+%28%222-s2.0-4243943295%22%29+OR+%28%222-s2.0-4944232881%22%29+OR+%28%222-s2.0-1842816907%22%29+OR+%28%222-s2.0-33646343022%22%29+OR+%28%222-s2.0-4143079428%22%29+OR+%28%222-s2.0-77954807118%22%29+OR+%28%222-s2.0-11544320930%22%29%29+AND+NOT+EID+%282-s2.0-84901638552%29&amp;origin=resultslist&amp;txGid=66816438A83B9A7B72A35BF872F8E10E.WeLimyRvBMk2ky9SFKc8Q%3a29"&gt;Related articles&lt;/a&gt;
